import sys
sys.path.append("..")

from lib import verticalPrint

def composition(k, text):
    #Returns all the k-mer substrings of text.

    # Input: An integer k and a string Text.
    # Output: Composition_k(Text), where the k-mers are arranged in lexicographic order.

    #Example: Composition3(TATGGGGTGC) = {ATG, GGG, GGG, GGT, GTG, TAT, TGC, TGG}.

    kmers = []
    for i in range(len(text) - k + 1):
        kmers.append(text[i:i+k])
    kmers.sort()
    return kmers

if __name__ == "__main__":
    verticalPrint(composition(100,""))